,ps.ARTICLE_ID as A_ID
,( SELECT COUNT(*) FROM zlp_article_comment c WHERE c.article_id = o.ARTICLE_ID ) AS COMMENT_COUNT from o LEFT JOIN zlp_app_user u on o.USER_ID=u.USER_ID LEFT JOIN zlp_goods g on o.GOODS_ID=g.GOODS_ID LEFT JOIN tb_pictures p on g.PICTURE_ID = p.PICTURES_ID LEFT JOIN tb_pictures p1 on u.PICTURE_ID = p1.PICTURES_ID LEFT JOIN zlp_article a on o.ARTICLE_ID = a.ARTICLE_ID
