mapper.select:
select , p.NICK_NAME, p.PHONE_NUMBER from o LEFT JOIN zlp_app_user p on o.USER_ID=p.USER_ID where 1=1
and (
p.NICK_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or p.PHONE_NUMBER LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and o.CREATE_DATE >= #{pd.lastStart}
and TO_DAYS(o.CREATE_DATE) <= TO_DAYS(#{pd.lastEnd})
and o.INCOME_REASON = #{pd.INCOME_REASON}
and o.USER_ID = #{pd.USER_ID}
order by o.CREATE_DATE DESC

select , DATE_FORMAT(o.CREATE_DATE,'%Y-%m-%d') as TIME from o where 1=1
and o.CREATE_DATE >= #{pd.lastStart}
and TO_DAYS(o.CREATE_DATE) <= TO_DAYS(#{pd.lastEnd})
and o.USER_ID = #{pd.USER_ID}
order by o.CREATE_DATE DESC

select , DATE_FORMAT(o.CREATE_DATE,'%Y-%m-%d') as TIME, p.NICK_NAME, p.PHONE_NUMBER from o LEFT JOIN zlp_app_user p on o.USER_ID=p.USER_ID where 1=1
and (
p.NICK_NAME LIKE CONCAT(CONCAT('%', #{keywords}),'%') or p.PHONE_NUMBER LIKE CONCAT(CONCAT('%', #{keywords}),'%') )
and o.CREATE_DATE >= #{lastStart}
and TO_DAYS(o.CREATE_DATE) <= TO_DAYS(#{lastEnd})
and o.INCOME_REASON = #{INCOME_REASON}
and o.USER_ID = #{USER_ID}
order by o.CREATE_DATE DESC
limit #{start_number},#{SET_NUMBER}

select
sum(AMOUNT) as AMOUNT
from

where 1=1

	and  USER_ID = #{USER_ID}


	and  RECORD_TYPE = #{RECORD_TYPE}

and to_days(CREATE_DATE) = to_days(now());
