mapper.select:
select

from

where 1=1

and ORDER_ID = #{ORDER_ID}


and OUT_ORDER_ID = #{OUT_ORDER_ID}


and USER_ID = #{USER_ID}

select , a1.FULL_NAME AS AREA, a2.FULL_NAME AS CITY, a3.FULL_NAME AS PROVINCE, e.NAME as COMPANY_NAME from o LEFT JOIN zlp_app_user p on o.USER_ID=p.USER_ID LEFT JOIN zlp_area a1 on o.AREA_ID = a1.AREA_ID LEFT JOIN zlp_area a2 on a2.AREA_ID = a1.PARENT_ID LEFT JOIN zlp_area a3 on a3.AREA_ID = a2.PARENT_ID LEFT JOIN zlp_express_company e on o.EXPRESSCOMPANY_ID=e.EXPRESSCOMPANY_ID where 1=1
and (
o.RECEIVER LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or e.NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or o.DETAILS_ADDRESS LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or p.PHONE_NUMBER LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and o.CREATE_DATE >= #{pd.lastStart}
and TO_DAYS(o.CREATE_DATE) <= TO_DAYS(#{pd.lastEnd})
and o.EXPRESSCOMPANY_ID = #{pd.EXPRESSCOMPANY_ID}
and o.PAY_WAY = #{pd.PAY_WAY}
and o.ORDER_STATUS = #{pd.ORDER_STATUS}
and o.DELETE_STATUS = #{pd.DELETE_STATUS}
and o.USER_ID = #{pd.USER_ID}
and o.ORDER_POSITION = #{pd.ORDER_POSITION}
order by o.CREATE_DATE DESC

