and (
o.RECEIVER LIKE CONCAT(CONCAT('%', #{keywords}),'%') or e.NAME LIKE CONCAT(CONCAT('%', #{keywords}),'%') or o.DETAILS_ADDRESS LIKE CONCAT(CONCAT('%', #{keywords}),'%') or p.PHONE_NUMBER LIKE CONCAT(CONCAT('%', #{keywords}),'%') )
