mapper.select:
select
,
p.PATH
from
 o
LEFT JOIN tb_pictures p on o.PICTURE_ID=p.PICTURES_ID
where 1=1

		and	o.USER_ID = #{USER_ID}


		and	o.PHONE_NUMBER = #{PHONE_NUMBER}


		and	o.INVITE_CODE = #{INVITE_CODE}


		and	o.PASSWORD = #{PASSWORD}


		and	o.ROLE = #{ROLE}


		and	o.TOKEN = #{TOKEN}


		and	o.OPENID = #{OPENID}

SELECT t1.USER_ID, t1.PHONE_NUMBER, t1.NICK_NAME, t1.INVITE_CODE, t1.PARENT_ID, t1.ROLE, t1.CREATE_DATE, t1.CREATE_USER, t1.UPDATE_DATE, t1.UPDATE_USER, t1.DELETE_STATUS, t1.PASSWORD, t1.PICTURE_ID, t1.SEX, t1.INTEGRATION, t1.PARTNER_USER_ID, t1.MONEY, t1.SOLAR_BIRTHDAY, t1.LUNAR_BRITHDAY, t1.NEXT_LUNAR_BRITHDAY, t1.NEXT_SOLAR_BRITHDAY, t1.REMIND_TYPE, t1.ALIPAY, t1.NEXT_LB_DAYS, t1.NEXT_SB_DAYS, t1.ADVANCE_DATE_COUNT, t2.nick_name AS PARENT_NAME, t3.nick_name AS PARTNER_NAME, t2.phone_number as PARENT_PHONE_NUMBER, t3.phone_number as PARTNER_PHONE_NUMBER, p.PATH FROM zlp_app_user t1 LEFT JOIN zlp_app_user t2 ON t1.parent_id = t2.user_id LEFT JOIN zlp_app_user t3 ON t1.partner_user_id = t3.user_id LEFT JOIN tb_pictures p ON t1.picture_id = p.pictures_id WHERE 1=1
and (
t1.NICK_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and t1.create_date >= #{pd.lastStart}
and t1.TO_DAYS(create_date) <= TO_DAYS(#{pd.lastEnd})
and t1.PARENT_ID = #{pd.USER_ID}
and t1.ROLE = #{pd.role}
and t1.SEX = #{pd.sex}
ORDER BY t1.create_date desc

mapper.update:
update

set
	INTEGRATION = INTEGRATION - #{INTEGRATION}
where
	USER_ID = #{USER_ID}
